function constructAlarmAddressSpace(server, addressSpace, eventObjects, done) {
  const opcua = coreServer.choreCompact.opcua;
  const LocalizedText = opcua.LocalizedText;
  const namespace = addressSpace.getOwnNamespace();

  const Variant = opcua.Variant;
  const DataType = opcua.DataType;
  const DataValue = opcua.DataValue;

  var flexServerInternals = this;

  // Initialize tank simulation variables
  this.sandboxFlowContext.set("currentVolume", 0.0);
  this.sandboxFlowContext.set("activeTankCount", 2.0);
  this.sandboxFlowContext.set("pumpStatus", 1.0);
  this.sandboxFlowContext.set("fabOutflowRate", 100.0);
  this.sandboxFlowContext.set("totalInflowRate", 0.0);
  this.sandboxFlowContext.set("totalOutflowRate", 0.0);
  this.sandboxFlowContext.set("simulationTime", 0.0);

  coreServer.debugLog("init dynamic address space");
  const rootFolder = addressSpace.findNode("RootFolder");

  node.warn("construct new address space for OPC UA");

  // Create main device folder
  const liftStation = namespace.addFolder(rootFolder.objects, {
    "browseName": "LiftStation"
  });

  // Create subfolders for organization
  const processData = namespace.addFolder(liftStation, {
    "browseName": "ProcessData"
  });
  const controlInputs = namespace.addFolder(liftStation, {
    "browseName": "ControlInputs"
  });
  const calculatedValues = namespace.addFolder(liftStation, {
    "browseName": "CalculatedValues"
  });

  // Process Data Variables (Read-Only)
  const currentVolume = namespace.addVariable({
    "organizedBy": processData,
    "browseName": "CurrentVolume",
    "nodeId": "ns=1;s=LiftStation.CurrentVolume",
    "dataType": "Double",
    "value": {
      "get": function () {
        return new Variant({
          "dataType": DataType.Double,
          "value": flexServerInternals.sandboxFlowContext.get("currentVolume")
        });
      },
      "set": function (variant) {
        flexServerInternals.sandboxFlowContext.set(
          "currentVolume",
          parseFloat(variant.value)
        );
        return opcua.StatusCodes.Good;
      }
    }
  });

  const simulationTime = namespace.addVariable({
    "organizedBy": processData,
    "browseName": "SimulationTime",
    "nodeId": "ns=1;s=LiftStation.SimulationTime",
    "dataType": "Double",
    "value": {
      "get": function () {
        return new Variant({
          "dataType": DataType.Double,
          "value": flexServerInternals.sandboxFlowContext.get("simulationTime")
        });
      },
      "set": function (variant) {
        flexServerInternals.sandboxFlowContext.set(
          "simulationTime",
          parseFloat(variant.value)
        );
        return opcua.StatusCodes.Good;
      }
    }
  });

  // Control Input Variables (Writable)
  const activeTankCount = namespace.addVariable({
    "organizedBy": controlInputs,
    "browseName": "ActiveTankCount",
    "nodeId": "ns=1;s=LiftStation.ActiveTankCount",
    "dataType": "Double",
    "value": {
      "get": function () {
        return new Variant({
          "dataType": DataType.Double,
          "value": flexServerInternals.sandboxFlowContext.get("activeTankCount")
        });
      },
      "set": function (variant) {
        flexServerInternals.sandboxFlowContext.set(
          "activeTankCount",
          parseFloat(variant.value)
        );
        return opcua.StatusCodes.Good;
      }
    }
  });

  const pumpStatus = namespace.addVariable({
    "organizedBy": controlInputs,
    "browseName": "PumpStatus",
    "nodeId": "ns=1;s=LiftStation.PumpStatus",
    "dataType": "Double",
    "value": {
      "get": function () {
        return new Variant({
          "dataType": DataType.Double,
          "value": flexServerInternals.sandboxFlowContext.get("pumpStatus")
        });
      },
      "set": function (variant) {
        flexServerInternals.sandboxFlowContext.set(
          "pumpStatus",
          parseFloat(variant.value)
        );
        return opcua.StatusCodes.Good;
      }
    }
  });

  const fabOutflowRate = namespace.addVariable({
    "organizedBy": controlInputs,
    "browseName": "FabOutflowRate",
    "nodeId": "ns=1;s=LiftStation.FabOutflowRate",
    "dataType": "Double",
    "value": {
      "get": function () {
        return new Variant({
          "dataType": DataType.Double,
          "value": flexServerInternals.sandboxFlowContext.get("fabOutflowRate")
        });
      },
      "set": function (variant) {
        flexServerInternals.sandboxFlowContext.set(
          "fabOutflowRate",
          parseFloat(variant.value)
        );
        return opcua.StatusCodes.Good;
      }
    }
  });

  // Calculated Values (Read-Only)
  const totalInflowRate = namespace.addVariable({
    "organizedBy": calculatedValues,
    "browseName": "TotalInflowRate",
    "nodeId": "ns=1;s=LiftStation.TotalInflowRate",
    "dataType": "Double",
    "value": {
      "get": function () {
        return new Variant({
          "dataType": DataType.Double,
          "value": flexServerInternals.sandboxFlowContext.get("totalInflowRate")
        });
      },
      "set": function (variant) {
        flexServerInternals.sandboxFlowContext.set(
          "totalInflowRate",
          parseFloat(variant.value)
        );
        return opcua.StatusCodes.Good;
      }
    }
  });

  const totalOutflowRate = namespace.addVariable({
    "organizedBy": calculatedValues,
    "browseName": "TotalOutflowRate",
    "nodeId": "ns=1;s=LiftStation.TotalOutflowRate",
    "dataType": "Double",
    "value": {
      "get": function () {
        return new Variant({
          "dataType": DataType.Double,
          "value": flexServerInternals.sandboxFlowContext.get("totalOutflowRate")
        });
      },
      "set": function (variant) {
        flexServerInternals.sandboxFlowContext.set(
          "totalOutflowRate",
          parseFloat(variant.value)
        );
        return opcua.StatusCodes.Good;
      }
    }
  });

  // Create views for easy access
  const viewProcessData = namespace.addView({
    "organizedBy": rootFolder.views,
    "browseName": "LiftStation-ProcessData"
  });

  const viewControlInputs = namespace.addView({
    "organizedBy": rootFolder.views,
    "browseName": "LiftStation-ControlInputs"
  });

  const viewCalculatedValues = namespace.addView({
    "organizedBy": rootFolder.views,
    "browseName": "LiftStation-CalculatedValues"
  });

  // Add references to views
  viewProcessData.addReference({
    "referenceType": "Organizes",
    "nodeId": currentVolume.nodeId
  });

  viewProcessData.addReference({
    "referenceType": "Organizes",
    "nodeId": simulationTime.nodeId
  });

  viewControlInputs.addReference({
    "referenceType": "Organizes",
    "nodeId": activeTankCount.nodeId
  });

  viewControlInputs.addReference({
    "referenceType": "Organizes",
    "nodeId": pumpStatus.nodeId
  });

  viewControlInputs.addReference({
    "referenceType": "Organizes",
    "nodeId": fabOutflowRate.nodeId
  });

  viewCalculatedValues.addReference({
    "referenceType": "Organizes",
    "nodeId": totalInflowRate.nodeId
  });

  viewCalculatedValues.addReference({
    "referenceType": "Organizes",
    "nodeId": totalOutflowRate.nodeId
  });

  coreServer.debugLog("create dynamic address space done");
  node.warn("construction of new address space for OPC UA done");

  done();
}

