// MQTT to Flow Context Mapper
// Subscribes to all lift station MQTT topics and updates flow variables

const topic = msg.topic;
const value = parseFloat(msg.payload);

// Validate that payload is a valid number
if (isNaN(value)) {
    node.warn(`Invalid numeric value received on ${topic}: ${msg.payload}`);
    return null;
}

// Map MQTT topics to flow context variables
switch (topic) {
    case "data/lift_station/current_volume":
        flow.set('currentVolume', value);
        node.status({ fill: "blue", shape: "dot", text: `Volume: ${value.toFixed(2)} L` });
        break;

    case "lift_station/active_tanks":
        flow.set('activeTankCount', value);
        node.status({ fill: "green", shape: "dot", text: `Active Tanks: ${value}` });
        break;

    case "lift_station/pump_status":
        flow.set('pumpStatus', value);
        const pumpState = value === 1.0 ? "ON" : "OFF";
        node.status({ fill: value === 1.0 ? "green" : "red", shape: "dot", text: `Pump: ${pumpState}` });
        break;

    case "lift_station/fab_outflow":
        flow.set('fabOutflowRate', value);
        node.status({ fill: "yellow", shape: "dot", text: `Fab Rate: ${value.toFixed(1)} L/min` });
        break;

    default:
        node.warn(`Unknown topic: ${topic}`);
        return null;
}

// Optional: Calculate derived values
const activeTanks = flow.get('activeTankCount') || 0;
const fabRate = flow.get('fabOutflowRate') || 0;
const pumpStat = flow.get('pumpStatus') || 0;
const pumpFlowRate = 60.0; // L/min (constant from simulation)

const totalInflow = fabRate * activeTanks;
const totalOutflow = pumpFlowRate * pumpStat;

flow.set('totalInflowRate', totalInflow);
flow.set('totalOutflowRate', totalOutflow);

// Pass through the message for debugging or chaining
msg.flowContext = {
    currentVolume: flow.get('currentVolume'),
    activeTankCount: flow.get('activeTankCount'),
    pumpStatus: flow.get('pumpStatus'),
    fabOutflowRate: flow.get('fabOutflowRate'),
    totalInflowRate: flow.get('totalInflowRate'),
    totalOutflowRate: flow.get('totalOutflowRate')
};

return msg;
